function initialize(){var e={center:new google.maps.LatLng(49.281834,-123.122557),zoom:14};map=new google.maps.Map(document.getElementById("map-canvas"),e);var a={location:e.center,radius:5e3,types:["restaurant"]};infowindow=new google.maps.InfoWindow;var n=new google.maps.places.PlacesService(map);n.nearbySearch(a,callback)}function callback(e,a){if(a==google.maps.places.PlacesServiceStatus.OK)for(var n=0;n<e.length;n++)createMarker(e[n])}function createMarker(e){var a=e.geometry.location,n=new google.maps.Marker({map:map,position:a});google.maps.event.addListener(n,"click",function(){infowindow.setContent(e.name),infowindow.open(map,this)})}var map,infowindow;google.maps.event.addDomListener(window,"load",initialize);